import { useContext, createContext, useState, useEffect } from "react";

const CartContext = createContext();
const [getCartCount, setCartCount] = useState(0)
useEffect(()=>{
    const localData = localStorage.getItem("cartItem");
    if(localData){
      const parsedData = JSON.parse(localData);
      setCartCount(parsedData.length)
    }
}) 

const CartContextProvider = ({children})=>{
    
    const [cartCount, setCount]= useState(getCartCount)
    return(
        <CartContext.Provider value={{cartCount, setCount}}>
            {children}
        </CartContext.Provider>
    )
}

const useCartContext = ()=>{
    return(
        useContext(CartContext)
    )
}

export {CartContextProvider, useCartContext}
