import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { serverDomain } from '../credentials.js';

export default function Categories() {
  const [data, setData]= useState([]);
  const [message, setMessage] = useState("")

  const getCategories = ()=>{
    axios({
      url: `${serverDomain}/category/view/`,
      method: "post",
      data: {

      }
    }).then((res)=>{
      setData(res.data)
    }).catch((err)=>{
      setMessage("There is an error fetching data, plesse try again later")
    })
  }

  useEffect(()=>{
    getCategories()
  },[])

  const showCategories = ()=>{
    data.map((item, index)=>{
      return(
        <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
          <img src="http://localhost:3001/images/categories/1690361900353-deserts-cat.png" alt={item.name}/>
        </div>
      )
    })
  }

  return (
    <>
    <div className="categories-section">
      <div className="container">
        <div className="sec-heading m-auto mb-5 text-center col-md-7">
          <h2>Choose from our best selling categories</h2>
        </div>
        <div className="row">
          <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
            <img src="./assets/images/pizza-cat.png" alt="pizza"/>
          </div>
          <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
            <img src="./assets/images/burger-cat.png" alt="burger"/>
          </div>
          <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
            <img src="./assets/images/noodles-cat.png" alt="noodles"/>
          </div>
          <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
            <img src="./assets/images/fries-cat.png" alt="fries"/>
          </div>
          <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
            <img src="./assets/images/waffles-cat.png" alt="waffles"/>
          </div>
          <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
            <img src="./assets/images/shakes-cat.png" alt="shakes"/>
          </div>
          <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
            <img src="./assets/images/pasta-cat.png" alt="pasta"/>
          </div>
          <div className="col-lg-3 col-md-4 col-sm-6 col-6 categories-card">
            <img src="./assets/images/deserts-cat.png" alt="deserts"/>
          </div>
        </div>
      </div>
    </div>
    </>
  )
}
