import React, {useState, useEffect} from 'react';
import { useParams } from 'react-router-dom';
import { serverDomain } from '../credentials.js';
import axios from 'axios';

export default function ProductsArchive(props) {
  const itemStatusData = props.itemStatus;

 const {name} = useParams();
 let catId = "";
 let dataFilter = {}
  if(name){
    catId = name.replace(':', '');
    dataFilter = {
      categoryId: catId
    } 
  }else{
    dataFilter = {}
  }

      useEffect(()=>{
      if(itemStatusData!=undefined && itemStatusData[0].veg===true){
        console.log("veg")
        dataFilter['veg']=true
      }
      else if(itemStatusData!=undefined && itemStatusData[0].veg===false){
        console.log("nonvef")
        dataFilter['veg']=false
      }else{
        dataFilter = {}
      }
      },[props.itemStatus])



 const [data, setData]= useState([])
  const getProducts = ()=>{
    console.log(dataFilter)
    axios({
      url: `${serverDomain}/product/view/`,
      method: "post",
      data: dataFilter

    }).then((res)=>{
      setData(res.data)
    }).catch((err)=>{
      // setMessage("There is an error fetching data, plesse try again later")
    })
  }
  useEffect(()=>{
    getProducts()
  },[props.itemStatus])
  

  const showProducts = ()=>{
    if(data.length>0){
      return(
        data.map((item, index)=>{
          return(
            <div className="col-lg-4 col-md-6 col-sm-6 product-card-wrap">
              <div className="product-card">
                <div className="image">
                  <img src="images/products-image_03.png" alt="product-image" />
                  <span className="sale-icon">15% OFF</span>
                </div>
                <div className="product-info">
                  <div className="product-name d-flex">
                    <div className="veg-status">
                      <img src="images/veg.png" alt="veg icon" />
                    </div>
                    <span>{item.name}</span>
                  </div>
                  <p>Pancake with fully added cheese and coverd with pizza</p>
                  <div className="product-bottom d-flex justify-content-between align-items-center">
                    <div className="sale">
                      <span className="line-through">{item.price}</span>
                      <span className="sale-amount">₹499</span>
                    </div>
                    <button className="btn btn-primary order-now">Order Now</button>
                  </div>
                </div>
              </div>
            </div>
          )
        })
      )
    }else{
      return(<h3 className="text-center">No Data Found</h3>)
    }
  }


return (
<>
      <div className="sorting-section">
        <span>Sort By: </span>
        {/* <select name="languages" id="lang" value="c#">
          <option value="javascript">JavaScript</option>
          <option value="php">PHP</option>
          <option value="java">Java</option>
          <option value="golang">Golang</option>
          <option value="python">Python</option>
          <option value="c#">C#</option>
          <option value="C++">C++</option>
          <option value="erlang">Erlang</option>
        </select> */}
      </div>
      <div className="products-wrap">
        <div className="row">
          
            {showProducts()}
        
        </div>
      </div>
</>
)
}
