import React, { useEffect, useState } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import { useNavigate } from "react-router-dom";

export default function ChangePassDash() {
    const Navigate = useNavigate()
  const [data, setData] = useState([]);
  const[pass,setPass] = useState("")
  const[confirmPass,setConfirmPass] = useState("")

  const getData = () => {
    axios({
      url: "http://localhost:3001/address/view/",
      method: "post",
      data: { _id: Cookies.get("userId") },
    })
      .then((data) => {
        setData(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    getData();
  }, []);

  const handleUpdate = (e)=>{
    console.log(pass)
    e.preventDefault();
    if( pass !== confirmPass){
        alert('Password does not match')
        return;
    }
    else if(pass=="" && confirmPass==""){
        alert('Password Field is blank')
    }
    else{
        console.log("asd")
        // axios({
        //     url: "http://localhost:3001/user/change-pass/",
        //     mehtod:"post",
        //     data : {
        //             filter: {
        //               id: Cookies.get("userId")
        //             },data: {
        //               password: confirmPass
        //             }
                  
        //     }
        // }).then(()=>{
        //     Navigate("/dashboard/profile")
        //     alert("Password Updated Succesfully")
        // })
    }
  }
  
  return (
    <>
        <h3 className="mb-4">Change Password</h3>

      {data.map((item, index) => {
        return (
          <div className="profile">
            <div className="row">
              <div className="col-md-12">
                <div className="d-flex">
                  <span>Name Password: </span>
                  <input type="password"className="pass mb-3" onChange={(e)=>{setPass(e.target.value)}}></input>
                </div>
                <div className="d-flex">
                  <span>Confirm Password: </span>
                  <input type="password"className="pass mb-3" onChange={(e)=>{setConfirmPass(e.target.value)}}></input>
                </div>
                <button style={{width: "auto", color: "#fff"}} class="button" onClick={handleUpdate}>Update</button>

              </div>
              
            </div>
          </div>
        );
      })}
    </>
  );
}
